INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (19, 1, '廉远堂高|看风驶篷|升山采珠|大有径庭|正色立朝', '远看山有色');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (20, 1, '相亲相近|即温听厉|置水之情|古井无波|浮声切响', '近听水无声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (21, 1, '月旦春秋|挂冠而去|簇锦团花|返本还元|诽誉在俗', '春去花还在');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (22, 1, '从井救人|浮来暂去|池鱼笼鸟|百不当一|胆丧魂惊', '人来鸟不惊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (23, 1, '一床两好|踟躇不前|慈明无双|月旦春秋|韬曜含光', '床前明月光');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (24, 1, '疑似之间|师心自是|参天两地|冠上加冠|沐露沾霜', '疑是地上霜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (25, 1, '举首奋臂|垂头塌翅|望尘G声|明光铮亮|日月其除', '举头望明月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (26, 1, '低头丧气|豹头环眼|怨旷思归|狂奴故态|心乡往之', '低头思故乡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (27, 1, '春诵夏弦|行眠立盹|不直一钱|不觉技痒|连更晓夜', '春眠不觉晓');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (28, 1, '安室利处|处堂燕鹊|寡闻少见|猿啼鹤唳|越鸟南栖', '处处闻啼鸟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (29, 1, '夜光之璧|七日来复|惊才风逸|飘风苦雨|一口同声', '夜来风雨声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (30, 1, '拈花一笑|崎吕落|知雄守雌|博物多闻|食少事繁', '花落知多少');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (31, 1, '草长莺飞|长揖不拜|迁莺出谷|飞鹰走狗|书通二酉|批风抹月|地坼天崩', '草长莺飞二月天');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (32, 1, '拂衣远去|堤溃蚁穴|杨穿三叶|眠花藉柳|心醉魂迷|漏春光|烟花风月', '拂堤杨柳醉春烟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (33, 1, '月下老儿|二童一马|行流散徙|学贯天人|怨旷思归|往蹇来连|文君早寡', '儿童散学归来早');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (34, 1, '忙投急趁|朝趁暮食|坦腹东床|风流蕴藉|放浪不羁|一纸千金|鸢肩豺目', '忙趁东风放纸鸢');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (35, 1, '洗兵牧马|鹤发童颜|善骑者堕|黄发台背|童牛角马', '牧童骑黄牛');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (36, 1, '言不逮意|死欲速朽|捉风捕影|鸡鸣而起|噤若寒蝉', '意欲捕鸣蝉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (37, 1, '祸生于忽|不轻然诺|闭门却轨|阖门百口|立少观多', '忽然闭口立');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (38, 1, '小德出入|投袂荷戈|倚马长才|危若朝露|舌尖口快|迫在眉尖|矫尾厉角', '小荷才露尖尖角');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (39, 1, '职名早投|赖有此耳|蜻蜓点水|蜻蜓点水|立命安身|发上指冠|搔头弄姿', '早有蜻蜓立上头');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (40, 1, '加人一等|十年磨剑|弱不好弄|桑榆之景|君子三戒|须发皆白|铭记不忘', '一年好景君须记');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (41, 1, '不耻最后|以是为恨|橙黄橘绿|苍黄翻覆|橙黄橘绿|惨绿年华|天行时气', '最是橙黄橘绿时');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (42, 1, '室迩人远|天上麒麟|寒蝉僵鸟|覆海移山|击石弹丝|径行直遂|旁逸斜出', '远上寒山石径斜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (43, 1, '白眉赤眼|楚天云雨|深闭固拒|有以善处|属垣有耳|达人知命|乔木世家', '白云深处有人家');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (44, 1, '手不停挥|悬车致仕|坐不窥堂|爱毛反裘|成语谜语|瑶林琼树|宫车晚出', '停车坐爱枫林晚');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (45, 1, '两鬓如霜|枝叶相持|红炉点雪|独异于人|百二金瓯|日月丽天|梨花带雨', '霜叶红于二月花');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (46, 1, '沉李浮瓜|拆白道字|乘龙配凤|借水行舟|飞将数奇|欲益反损|冥行`埴', '李白乘舟将欲行');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (47, 1, '瞻前忽后|独学寡闻|岸然道貌|上下同门|自相践踏|聋者之歌|同声相求', '忽闻岸上踏歌声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (48, 1, '桃李春风|月闭花羞|虎穴龙潭|盂方水方|雄深雅健|寸心千古|鱼肠尺素', '桃花潭水深千尺');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (49, 1, '不次之位|攘肌及骨|汪洋自恣|天伦之乐|雪里送炭|彼弃我取|情孚意合', '不及汪伦送我情');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (50, 1, '长安少年|小大由之|故宫离黍|浮家泛宅|狂吟老监|齐大非耦|挥戈回日', '少小离家老大回');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (51, 1, '心乡往之|梵呗圆音|贪贿无艺|改政移风|γ捣鬓|γ捣鬓|物极则衰', '乡音无改鬓毛衰');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (52, 1, '鬻儿卖女|黄童皓首|积不相能|览闻辩见|绝口不道|披心相付|高才远识', '儿童相见不相识');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (53, 1, '笑语指麾|六问三推|白丁俗客|靡所适从|无如之何|触处机来|往古来今', '笑问客从何处来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (54, 1, '弟子孩儿|黄童皓首|急处从宽|走身无路|卖笑追欢|数白论黄|庄周梦蝶', '儿童急走追黄蝶');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (55, 1, '腾声飞实|出口入耳|水菜不交|花遮柳隐|水米无交|野居穴处|祸乱相寻', '飞入菜花无处寻');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (56, 1, '钟离委珠|鸿离鱼网|n_在原|屋上建瓴|拨草瞻风', '离离原上草');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (57, 1, '赏一劝百|以日为岁|利出一孔|枯耘伤岁|雍荣华贵', '一岁一枯荣');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (58, 1, '野人献芹|束蕴乞火|烧犀观火|不存不济|尽态极妍', '野火烧不尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (59, 1, '春冰虎尾|风云月露|吹灰之力|损之又损|梦笔生花', '春风吹又生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (60, 1, '依违两可|又弱一个|丹黄甲乙|鹗心鹂舌|鸣珂锵玉|偎红倚翠|宠柳娇花', '两个黄鹂鸣翠柳');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (61, 1, '太仓一粟|庆吊不行|素车白马|闲鸥野鹭|颊上三毛|掇青拾紫|呵壁问天', '一行白鹭上青天');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (62, 1, '雪窗萤火|抱朴含真|西窗剪烛|翻山越岭|无千无万|秋风落叶|流风回雪', '窗含西岭千秋雪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (63, 1, '倚门倚闾|凤泊鸾漂|东西易面|吴下阿蒙|万目睽睽|表里受敌|逆风撑船', '门泊东吴万里船');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (64, 1, '日坐愁城|心照不宣|卖履分香|红炉点雪|齿颊生香|拖紫垂青|烟炎张天', '日照香炉生紫烟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (65, 1, '飞觞走|漱石枕流|质直浑厚|七行俱下|三年化碧|拨万轮千|尺水丈波', '飞流直下三千尺');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (66, 1, '狐疑未决|师心自是|粉妆银砌|河梁携手|磊落豪横|九转丸成|寸地尺天', '疑是银河落九天');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (67, 1, '超然独立|尽其在我|树同拔异|冥漠之乡|破觚为圜|探异玩奇|客死他乡', '独在异乡为异客');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (68, 1, '每下愈况|久别重逢|二八佳人|柏舟之节|身价百倍|熟思审处|蔼然可亲', '每逢佳节倍思亲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (69, 1, '看人眉睫|雍荣闲雅|折桂攀蟾|口吻生花|飘茵落溷', '人闲桂花落');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (70, 1, '愁多夜长|神闲气静|月旦春秋|名山事业|鉴空衡平', '夜静春山空');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (71, 1, '咏月嘲风|语出月胁|见善若惊|寿山福海|穷鸟入怀', '月出惊山鸟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (72, 1, '不入时宜|金鼓齐鸣|皮里春秋|餐松饮涧|执两用中', '时鸣春涧中');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (73, 1, '纸贵洛城|纸贵洛阳|戏彩娱亲|玉昆金友|风雨如磐|同美相妒|好问决疑', '洛阳亲友如相问');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (74, 1, '太仓一粟|片鳞残甲|钻冰求酥|刊心刻骨|三星在户|美如冠玉|壶浆塞道', '一片冰心在玉壶');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (75, 1, '拨万轮千|别具炉锤|万目睽睽|枘凿方圆|出入神鬼|艰深晦涩|山颓木坏', '千锤万凿出深山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (76, 1, '遗休余烈|不避汤火|焚香扫地|怒火中烧|深藏若虚|等闲人物|安闲自得', '烈火焚烧若等闲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (77, 1, '抟香弄粉|慎身修永|碎瓦颓垣|曝骨履肠|雀小脏全|迥不犹人|心惊胆怕', '粉身碎骨全不怕');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (78, 1, '不忘久要|雁足留书|闭壁清野|黑家白日|自繇自在|寸铁杀人|间不容砺', '要留清白在人间');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (79, 1, '咬文啮字|昏定晨省|青钱万选|拔山超海|绝口不道|放浪不拘|松形鹤骨', '咬定青山不放松');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (80, 1, '海立云垂|绝其本根|心满原足|腥闻在上|镜破钗分|岩居川观|曳尾涂中', '立根原在破岩中');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (81, 1, '赤口白舌|日坐愁城|放心解体|选歌试舞|须发皆白|纵情遂欲|玄酒瓠脯', '白日放歌须纵酒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (82, 1, '绿蓑青笠|富于春秋|蛮来生作|伴食中书|爱素好古|还元返本|乡壁虚造', '青春作伴好还乡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (83, 1, '先得我心|演古劝今|三星在天|公道大明|重迹屏气|精神抖擞|精神抖擞', '我劝天公重抖擞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (84, 1, '不即不离|不拘小节|定于一尊|格物致知|降志辱身|迥不犹人|尺二秀才', '不拘一格降人才');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (85, 1, '休戚相关|鸿鹄将至|白眉赤眼|披发左衽|低唱浅酌|数白论黄|鸡鸣戒旦', '休将白发唱黄鸡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (86, 1, '势若脱兔|手到拈来|石烂江枯|东南之宝|赶不上趟|天上麒麟|春露秋霜', '若到江南赶上春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (87, 1, '彪炳千古|无千待万|埙篪相和|齿牙春色|站不住脚', '千万和春住');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (88, 1, '惟所欲为|问牛知马|文行出处|独异于人|眼去眉来|一刹那间|不落边际', '欲问行人去那边');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (89, 1, '眉目如画|头晕眼昏|衅稔恶盈|保泰持盈|穴处知雨', '眉眼盈盈处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (90, 1, '遥遥相对|一事不知|如兄如弟|风月子弟|登高去梯|材高知深|出处语默', '遥知兄弟登高处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (91, 1, '不啻天渊|重门击柝|牖中窥日|目断鳞鸿|楚楚谡谡|沅江九肋|涣尔冰开', '天门中断楚江开');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (92, 1, '雕楹碧槛|海水群飞|角巾东路|逐流忘返|四至八道|诸如此例|德胜头回', '碧水东流至此回');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (93, 1, '两脚野狐|隔岸观火|白齿青眉|丘山之功|弹冠相庆|对证下药|六出奇计', '两岸青山相对出');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (94, 1, '白云孤飞|顺风扯帆|利出一孔|片甲无存|明日黄花|无边无涯|青蚨飞来', '孤帆一片日边来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (95, 1, '青肝碧血|金浆玉醴|妆聋做哑|橛守成规|劝百讽一|剑树刀山|柳絮才高', '碧玉妆成一树高');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (96, 1, '不根之谈|鲍子知我|才大心细|枝叶旁牒|莫敢谁何|独出心裁|神出鬼入', '不知细叶谁裁出');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (97, 1, '连二并三|嘲风弄月|料峭春寒|风流儒雅|侯门似海|剪烛西窗|刀头燕尾', '二月春风似剪刀');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (98, 1, '恬淡寡欲|把饭叫饥|东扶西倒|浪迹江湖|周而不比|东征西怨|凭虚公子', '欲把西湖比西子');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (99, 1, '恬淡无欲|浓妆艳饰|浓抹淡妆|拐弯抹角|总角之交|相鼠有皮|随时施宜', '淡妆浓抹总相宜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (100, 1, '等价交换|雍容闲雅|识涂老马|求浆得酒|东量西折|刻雾裁风|白面书郎', '等闲识得东风面');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (101, 1, '万象森罗|拖青纡紫|千变万轸|绿惨红愁|总角相交|论列是非|春山如笑', '万紫千红总是春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (102, 1, '月里嫦娥|皓齿娥眉|逐机应变|追悔不及|苟且偷安|慧心灵性|不死之药', '嫦娥应悔偷灵药');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (103, 1, '碧空如洗|群鸿戏海|朱阁青楼|经纬天下|夜以继昼|长夜之饮|肝心若裂', '碧海青天夜夜心');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (104, 1, '横赋暴敛|看破红尘|鼎成龙升|e冈复岭|卧榻之侧|负土成坟|登峰造极', '横看成岭侧成峰');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (105, 1, '轻举远游|远亲近友|高冠博带|低情曲意|各色人等|百不当一|上下同门', '远近高低各不同');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (106, 1, '匕鬯不惊|爱口识羞|初出茅庐|山肴野蔌|真伪难辨|桃花人面|戟指怒目', '不识庐山真面目');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (107, 1, '凤只鸾孤|缘鹄饰玉|佻身飞镞|恫在抱|赖有此耳|山崩钟应|玉毁椟中', '只缘身在此山中');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (108, 1, '山珍海错|重厚少文|登山临水|顾复之恩|居之不疑|殆无虚日|道路侧目', '山重水复疑无路');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (109, 1, '柳啼花怨|暗室亏心|驿路梅花|含明隐迹|损之又损|日长一线|村夫俗子', '柳暗花明又一村');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (110, 1, '安堵如故|居停主人|东张西张|谊不敢辞|丹黄甲乙|槛花笼鹤|楼船箫鼓', '故人西辞黄鹤楼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (111, 1, '烟视媚行|簇锦团花|草庐三顾|犀牛望月|赧颜汗下|游光扬声|青州从事', '烟花三月下扬州');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (112, 1, '矜寡孤独|占风使帆|近悦远来|捉风捕影|桑田碧海|见惯司空|扫地以尽', '孤帆远影碧空尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (113, 1, '进退唯谷|见性成佛|扣壶长吟|江淹梦笔|昊天罔极|不落边际|积厚流光', '唯见长江天际流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (114, 1, '畸流逸客|舍生取谊|白齿青眉|青裙缟袂|桃夭柳媚|正色立朝|革旧鼎新', '客舍青青柳色新');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (115, 1, '劝善惩恶|大人君子|寒灰更燃|吃著不尽|劝百讽一|杯酒戈矛|甘酒嗜音', '劝君更尽一杯酒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (116, 1, '东猎西渔|利出一孔|朝阳丹凤|关门闭户|贵贱无二|故剑情深|看人眉睫', '西出阳关无故人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (117, 1, '同美相妒|另眼看待|一蛇两头|苗而不秀|淡而不厌', '相看两不厌');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (118, 1, '只轮莫返|腹有鳞甲|梁孟相敬|长亭短亭|累土至山', '只有敬亭山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (119, 1, '覆亡无日|出死断亡|石烂江枯|火烛银花|红装素裹|战胜攻取|石火电光', '日出江花红胜火');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (120, 1, '回春之术|来轸方遒|江河行地|海水群飞|绿肥红瘦|春山如笑|衣掷堵', '春来江水绿如蓝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (121, 1, '东诓西骗|闭目塞聪|升山采珠|前言往行|精贯白日|闲鸥野鹭|燕雁代飞', '西塞山前白鹭飞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (122, 1, '河斜月落|代马依风|细入毫芒|箕风毕雨|匕鬯不惊|不时之须|天命有归', '斜风细雨不须归');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (123, 1, '研京练都|枉口拔舌|豆剖瓜分|五洲四海|利出一孔|走花溜水|间不容砺', '京口瓜洲一水间');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (124, 1, '钟鼓之色|剑树刀山|只鸡斗酒|隔宿之仇|屈指可数|重足屏息|出山泉水', '钟山只隔数重山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (125, 1, '如坐春风|春风沂水|损之又损|绿林强盗|天堑长江|斗南一人|岸谷之变', '春风又绿江南岸');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (126, 1, '慈明无双|月夕花朝|莫之谁何|与时偕行|肝胆相照|鲍子知我|要价还价', '明月何时照我还');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (127, 1, '沓冈复岭|白色恐怖|匆匆忙忙|行色匆匆|梦中说梦|不绝如发|一座尽倾', '复恐匆匆说不尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (128, 1, '江河行地|瘠人肥己|临文不讳|诗书发冢|又作别论|开柙出虎|锡衮封圭', '行人临发又开封');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (129, 1, '齐大非偶|父紫儿朱|锄强扶弱|觞酒豆肉|斩岸堙溪|东方千骑', '大儿锄豆溪东');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (130, 1, '舟中敌国|弟子孩儿|直言正谏|男耕女织|鸡尸牛从|笼鸟池鱼', '中儿正织鸡笼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (131, 1, '不耻最后|惊喜交集|知小言大|卖儿贴妇|三户亡秦|百无聊赖', '最喜小儿亡赖');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (132, 1, '妇姑勃溪|草头天子|安枕而卧|剥床及肤|柳腰莲脸|断梗飞蓬', '溪头卧剥莲蓬');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (133, 1, '好勇斗狠|雨]云u|一知半见|小时了了|竭诚尽节', '好雨知时节');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (134, 1, '秉政当轴|月旦春秋|乃文乃武|蹈厉奋发|利用厚生', '当春乃发生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (135, 1, '随者唱喁|嘲风弄月|潜深伏T|尽入彀中|暮夜怀金', '随风潜入夜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (136, 1, '润笔之绢|格物致知|微过细故|殆无孑遗|偷声细气', '润物细无声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (137, 1, '救命稻草|扬花M藻|倚玉偎香|表里山河|和颜说色|丰肌秀骨|逾年历岁', '稻花香里说丰年');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (138, 1, '内视反听|取诸宫中|蛙鸣鸱叫|名声籍甚|千里一曲|冰心一片', '听取蛙声一片');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (139, 1, '屋如七星|才占八斗|个人崇拜|星奔电迈|炼石补天|闳中肆外', '七八个星天外');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (140, 1, '磨盘两圆|三坟五典|装点一新|雨丝风片|倒山倾海|前危后则', '两三点雨山前');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (141, 1, '张红燃爆|垂名竹帛|一口同声|居轴处中|千里一曲|积日累岁|除恶务本', '爆竹声中一岁除');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (142, 1, '春葩丽藻|肺石风清|迎来送往|日暖风和|出口入耳|屠毒笔墨|苏武牧羊', '春风送暖入屠苏');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (143, 1, '烧琴煮鹤|糠豆不赡|燃萁煎豆|红豆相思|燃萁煎豆', '煮豆燃豆萁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (144, 1, '箪食豆羹|在家出家|鱼游釜内|进退中绳|牛衣对泣', '豆在釜中泣');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (145, 1, '务本力穑|习非胜是|同恶共济|扎根串连|春生秋杀', '本是同根生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (146, 1, '埙篪相和|以煎止燔|有何面目|龙泉太阿|急景雕年', '相煎何太急');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (150, 2, '荷枪实弹|扫地以尽|恶贯已盈|不无小补|众擎易举|尤云雨|倾盖之交', '荷尽已无擎雨盖');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (151, 2, '春兰秋菊|除残去秽|犹解倒悬|相惊伯有|谑浪笑傲|露往霜来|千仞无枝', '菊残犹有傲霜枝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (152, 2, '定于一尊|年丰岁稔|爱素好古|追风摄景|民贵君轻|煮粥焚须|记功忘过', '一年好景君须记');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (153, 2, '不耻最后|如是我闻|橙黄橘绿|龙血玄黄|橘化为枳|绿惨红销|遭时定制', '最是橙黄橘绿时');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (154, 2, '依人篱下|金瓶落井|学疏才浅|布衣疏食|机杼一家|羊肠小径|探赜钩深', '篱落疏疏一径深');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (155, 2, '树俗立化|两头白面|月闭花羞|落雁沉鱼|沉吟未决|玉汝于成|寸阴若岁', '树头花落未成阴');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (156, 2, '超然远引|芬芳馥郁|不可侵犯|超今越古|道存目击', '远芳侵古道');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (157, 2, '画龙点晴|珠歌翠舞|连三接二|淫荒无度|国之干城', '晴翠接荒城');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (158, 2, '又惊又喜|迎新送故|王佐之材|子孙后代|归去来兮', '又送王孙去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (159, 2, '贝锦萋菲|萋斐贝锦|誉满寰中|别类分门|情见势屈', '萋萋满别情');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (160, 2, '囊箧萧条|祸生萧墙|支支梧梧|枝叶相持|水送山迎|神湛骨寒|嗳声叹气', '萧萧梧叶送寒声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (161, 2, '天堑长江|拔宅上升|皮里春秋|刻雾裁风|无动为大|坐客无毡|情投意', '江上秋风动客情');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (162, 2, '知我罪我|动静有常|穷儿暴富|返老还童|挑唇料嘴|局天促地|断织劝学', '知有儿童挑促织');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (163, 2, '炫昼缟夜|情深友于|傍人篱落|遗落世事|加人一等|灯火辉煌|神而明之', '夜深篱落一灯明');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (164, 2, '饥寒交切|五风十雨|连畴接陇|枕山臂江|夙夜在公|返虚入浑|宋画吴冶', '寒雨连江夜入吴');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (165, 2, '阿平绝倒|察察而明|送故迎新|客夺主|楚歌四合|山肴野蔌|孤悬浮寄', '平明送客楚山孤');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (166, 2, '黄耳寄书|三番四覆|徐娘半老|自家撞著|流水桃花|二满三平|独辟蹊径', '黄四娘家花满蹊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (167, 2, '千万买邻|如花似朵|亿万斯年|朵颐大嚼|以势压人|枝词蔓语|低头丧气', '千朵万朵压枝低');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (168, 2, '迁延羁留|连更星夜|偷狗戏鸡|游蜂戏蝶|动不失时|通时合变|缓歌缦舞', '留连戏蝶时时舞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (169, 2, '斤斤自守|在在皆是|娇小玲珑|柳莺花燕|恰中要害|恰中要害|莺啼燕语', '自在娇莺恰恰啼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (170, 2, '壁立千仞|耳鬓撕磨|万象森罗|击钟鼎食|还年却老|靡坚不摧|清风劲节', '千磨万击还坚劲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (171, 2, '沈诗任笔|聊复尔尔|避世墙东|东量西折|南舣北驾|必不挠北|流风余韵', '任尔东西南北风');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (172, 2, '初来乍到|独学寡闻|感激流涕|新亭之泪|秤平斗满|沐猴衣冠|裂裳衣疮', '初闻涕泪满衣裳');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (173, 2, '杜门却扫|看杀卫d|贤母良妻|经史百子|愁潘病沈|无计奈何|腥闻在上', '却看妻子愁何在');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (174, 2, '漫天蔽野|噤口卷舌|诗情画意|悬石程书|回_作喜|惟所欲为|如醉如狂', '漫卷诗书喜欲狂');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (175, 2, '即物穷理|鸡尸牛从|巴三览四|倒峡泻河|植发穿冠|巫山云雨|排山倒峡', '即从巴峡穿巫峡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (176, 2, '触机便发|床下牛斗|怀山襄陵|阳解阴毒|折节向学|巫山洛浦|潜窃阳剽', '便下襄阳向洛阳');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (177, 2, '言十妄九|神州华胄|嵩生岳降|神清气茂|恃强倚宠|风栉雨沐|电闪雷鸣', '九州生气恃风雷');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (178, 2, '一彻万融|不食马肝|比肩齐声|万马皆喑|追根究蒂|巧不可阶|哀告宾服', '万马齐喑究可哀');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (179, 2, '鹿死谁手|绝口不道|蒹葭伊人|生众食寡|安忍无亲|枯枝再春|木强少文', '谁道人生无再少');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (180, 2, '当门抵户|后拥前遮|流离播迁|水米无交|疏财尚气|粥粥无能|东劳西燕', '门前流水尚能西');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (181, 2, '拔地摇山|巴人下里|蕙质兰心|成语故事|鹤短凫长|浸明浸昌|妇姑勃溪', '山下兰芽短浸溪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (182, 2, '松松垮垮|郑卫桑间|聚沙之年|不讳之路|净几明窗|规求无度|泥扬波', '松间沙路净无泥');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (183, 2, '风流潇洒|风流潇洒|暮爨朝舂|暑雨祁寒|无肠公子|重规迭矩|啼天哭地', '潇潇暮雨子规啼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (184, 2, '风行水上|以是为恨|耳热眼花|无风扬波|捭阖纵横', '水是眼波横');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (185, 2, '排山压卵|改是成非|n首信眉|横峰侧岭|聚讼纷纭', '山是眉峰聚');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (186, 2, '沿才授职|下车之始|送往视居|皮里春秋|众流归海', '才始送春归');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (187, 2, '又红又专|目送手挥|与君周旋|企而望归|破壁飞去', '又送君归去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (188, 2, '东山高卧|色仁行违|司空眼惯|发蒙启滞|五风十雨|死亦瞑目|诡形奇制', '山色空蒙雨亦奇');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (189, 2, '指不胜偻|补天浴日|哑子寻梦|流芳万古|涕泗纵横|悲歌易水|率土之滨', '胜日寻芳泗水滨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (190, 2, '举无遗策|一望无边|韬光用晦|遁阴匿景|青山一发|利时及物|除秽布新', '无边光景一时新');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (191, 2, '七孔生烟|昼乾夕惕|往古来今|连宵达旦|雾里看花|朱甍碧瓦|凌霄之志', '七夕今宵看碧霄');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (192, 2, '系颈牵羊|问牛知马|男耕女织|女大须嫁|暗渡陈仓|河梁携手|遇水迭桥', '牵牛织女渡河桥');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (193, 2, '亡国破家|黑家白日|乞儿乘车|巧作名目|望尘靡及|多故之秋|风前月下', '家家乞巧望秋月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (194, 2, '贯穿融会|知尽能索|碧鬟红袖|毫发丝粟|相去无几|拨万轮千|金科玉条', '穿尽红丝几万条');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (195, 2, '白云孤飞|孟母三迁|蹑足屏息|风清弊绝|明烛天南|只影孤形|深惟重虑', '云母屏风烛影深');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (196, 2, '五言长城|悬河注火|杜渐防萌|驹齿未落|晓行夜宿|星行电征|沉吟未决', '长河渐落晓星沉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (197, 2, '张皇莫措|笑傲风月|不违农时|穰穰满家|剩汤腊水|羊羔美酒|浑然一体', '莫笑农家腊酒浑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (198, 2, '丰亨豫大|百年之柄|人死留名|客夺主|足音跫然|鸡鸣@耕|瘠牛偾豚', '丰年留客足鸡豚');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (199, 2, '剑态箫心|败鼓之皮|逐风追电|修短随化|沂水春风|村歌社舞|急公近利', '箫鼓追随春社近');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (200, 2, '粗衣粝食|冠盖如云|辞简义赅|朴实无华|古来今往|当风秉烛|目击道存', '衣冠简朴古风存');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (201, 2, '云集景从|行古志今|昂昂自若|何许人也|忙里偷闲|有可乘|日堙月塞', '从今若许闲乘月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (202, 2, '撑门拄户|束杖理民|衾影无惭|天行时气|昼夜不舍|叩石垦壤|柳户花门', '拄杖无时夜叩门');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (203, 2, '行师动众|穷鸟入怀|高冠博带|佻身飞镞|以尽吾齿', '众鸟高飞尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (204, 2, '幺弦孤韵|野鹤闲云|独木不林|返来复去|游手偷闲', '孤云独去闲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (205, 2, '湖吃海喝|敛锷韬光|春蛇秋蚓|犀牛望月|一丝两气|浪子宰相|鸾凤和鸣', '湖光秋月两相和');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (206, 2, '一潭死水|南面百城|慈明无双|别风淮雨|明镜高悬|方滋未艾|耳鬓斯磨', '潭面无风镜未磨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (207, 2, '逍遥自在|望岫息心|七穿八洞|门庭赫奕|藏之名山|马水车龙|色艺两绝', '遥望洞庭山水色');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (208, 2, '白首相知|银钩玉唾|盘游无度|鞭辟向里|机杼一家|青钱万选|吹大法螺', '白银盘里一青螺');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (209, 2, '愁红怨绿|遍地开花|表里山河|原始见终|白衣公卿|满坑满谷|众川赴海', '绿遍山原白满川');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (210, 2, '骥子龙文|以规为|金声玉润|表里山河|别风淮雨|风雨如磐|烟炎张天', '子规声里雨如烟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (211, 2, '乡风慕义|村歌社舞|三反四覆|花朝月夜|钻懒帮闲|非异人任|少气无力', '乡村四月闲人少');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (212, 2, '倚马长才|了了可见|老蚕作茧|鸡犬桑麻|损之又损|插翅难飞|渤噬Ｌ', '才了蚕桑又插田');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (213, 2, '不舍昼夜|当行出色|耘失岁|焚林而田|昼日昼夜|丰烈伟绩|拽布披麻', '昼出耘田夜绩麻');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (214, 2, '村夫俗子|项庄舞剑|喔咿儒儿|女大难留|各安本业|锋不可当|家鸡野鹜', '村庄儿女各当家');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (215, 2, '齿豁头童|王孙公子|未明求衣|一知片解|供不应求|躬耕乐道|深文罗织', '童孙未解供耕织');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (216, 2, '之乎者也|傍人门户|桑榆之景|寸阴是惜|学行修明|多种多样|抱蔓摘瓜', '也傍桑阴学种瓜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (217, 2, '洛阳才子|丹凤朝阳|富面百城|鞭擗向里|见素抱朴|秋风纨扇|浇风薄俗', '洛阳城里见秋风');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (218, 2, '蛇欲吞象|执柯作伐|穰穰满家|书画卯酉|像心像意|明见万里|卑辞重币', '欲作家书意万重');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (219, 2, '卑身贱体|矛盾相向|豆重榆瞑|寝关曝纩|一刹那间|畔道离经|横行不法', '身向榆关那畔行');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (220, 2, '暮夜怀金|交浅言深|运筹千里|欺瞒夹帐|油干灯尽', '夜深千帐灯');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (221, 2, '聒聒噪噪|片鳞碎甲|他乡异县|腹诽心谤|如醉如梦|不分畛域|眩碧成朱', '聒碎乡心梦不成');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (222, 2, '故人之意|息影家园|国士无双|此呼彼应|大张声势', '故园无此声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (223, 2, '迷花沾草|镂金铺翠|横眉吐气|田父野叟|六根清静|七舌八嘴|千里一曲', '草铺横野六七里');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (224, 2, '虎年成语|抟沙弄汞|晚节不保|北风之恋|三星在天|拿三搬四|腾声飞实', '笛弄晚风三四声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (225, 2, '罪有攸归|古来今往|饱经世故|饭坑酒囊|黄龙痛饮|利令智昏|以为后图', '归来饱饭黄昏后');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (226, 2, '不可胜计|临阵脱逃|雨蓑烟笠|椎髻布衣|扳辕卧辙|日销月铄|明扬仄陋', '不脱蓑衣卧月明');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (227, 2, '拈花一笑|玉叶金柯|渔夺侵牟|引船就岸|参天两地|臧仓小人|鹤发童颜', '一叶渔船两小童');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (228, 2, '杂学旁收|成语谜语|鹄峙鸾停|成语谜语|稠人广坐|见风使船|此中三昧', '收篙停棹坐船中');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (229, 2, '邪魔怪道|嘘枯吹生|兼爱无私|袂云汗雨|清都紫府|张本继末|火伞高张', '怪生无雨都张伞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (230, 2, '不可胜计|从恶是崩|遮天盖日|方头不劣|休戚是同|使羊将狼|流风余韵', '不是遮头是使风');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (231, 2, '綦溪利|旁行斜上|平地青云|青史传名|羊真孔草', '溪上青青草');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (232, 2, '中心如醉|千里犹面|楚舞吴歌|濮上之音|蛮触相争|崇洋媚外|妍蚩好恶', '醉里吴音相媚好');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (233, 2, '葛巾野服|大相径庭|冬日夏云|声色俱厉|织白守黑', '野径云俱黑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (234, 2, '澄江如练|南船北马|钻冰取火|独具只眼|明耻教战', '江船火独明');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (235, 2, '夜宿晓行|下眼相看|绿惨红愁|上漏下湿|块然独处', '晓看红湿处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (236, 2, '花说柳说|重金袭汤|锦心绣口|进禄加官|龙楼凤城', '花重锦官城');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (237, 2, '明光锃亮|不日不月|别鹤孤鸾|枝附叶著|惊心褫魄|鹊巢鸠', '明月别枝惊鹊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (238, 2, '清酌庶羞|风情月债|一言半辞|衣锦夜游|戛玉鸣金|蝉联往复', '清风半夜鸣蝉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (239, 2, '革新变旧|运蹇时低|三顾茅庐|药店飞龙|村歌社鼓|寄迹山林|当耳边风', '旧时茅店社林边');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (240, 2, '来踪去路|旋干转坤|斩岸堙溪|头童齿豁|疏忽大意|寡闻少见', '路转溪头忽见');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (241, 2, '幺弦孤韵|村夫野老|发蒙振落|晖光日新|剩菜残羹|服气餐霞', '孤村落日残霞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (242, 2, '敖世轻物|灰烟瘴气|直壮曲老|琪花玉树|唇竭齿寒|鸦鹊无声', '轻烟老树寒鸦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (243, 2, '百不获一|点石为金|飞苍走黄|鸿雁哀鸣|鞭丝帽影|巴人下里', '一点飞鸿影下');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (244, 2, '白衣秀士|草靡风行|批红判白|莫辨楮叶|口中雌黄|六出冰花', '白草红叶黄花');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (245, 2, '总角之交|把薪助火|自新改过|流水桃花|鸟枪换炮|弃旧怜新|尺竹伍符', '总把新桃换旧符');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (246, 2, '美如冠玉|颗粒无收|铁网珊瑚|珊瑚在网|七行俱下|批风抹月|六趣轮回', '玉颗珊珊下月轮');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (247, 2, '剑履上殿|参前倚衡|拾遗补阙|七开八得|春露秋霜|轩裳华胄|新发于硎', '殿前拾得露华新');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (248, 2, '摩顶至足|尊古卑今|不世之功|贯通融会|大天白日|釜中游鱼|事核言直', '至今不会天中事');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (249, 2, '如应斯响|言是人非|嫦娥奔月|娥皇女英|鳌掷鲸|无与比伦|觉人觉世', '应是嫦娥掷与人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (250, 3, '孤标独步|名山事业|古寺青灯|马空冀北|直言贾祸|亭亭玉立|东怒西怨', '孤山寺北贾亭西');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (251, 3, '木本水源|拥书南面|初度之辰|平地登云|云树之思|点指划脚|浅斟低唱', '水面初平云脚低');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (252, 3, '相去无几|处实效功|早占勿药|燕语莺声|十手争指|日暖风和|别树一旗', '几处早莺争暖树');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (253, 3, '欲谁归罪|国尔忘家|新亭对泣|燕处危巢|燕啄皇孙|齿牙春色|庇袢缒', '谁家新燕啄春泥');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (254, 3, '拨乱反治|月夕花朝|情窦渐开|偿其大欲|执迷不返|觉人觉世|豹头环眼', '乱花渐欲迷人眼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (255, 3, '福浅命薄|草S禽A|扬己露才|矜名妒能|有嘴没心|马勃牛溲|马不停蹄', '浅草才能没马蹄');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (256, 3, '为善最乐|爱如己出|闯荡江湖|东床快婿|矩步方行|六尘不染|救经引足', '最爱湖东行不足');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (257, 3, '红愁绿惨|潘杨之睦|昼阴夜阳|皮里阳秋|白驹空谷|焦沙烂石|堤溃蚁孔', '绿杨阴里白沙堤');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (258, 3, '连明达夜|日月重光|朱紫难别|枝附叶著|愤风惊浪|鹊巢鸠占', '明月别枝惊鹊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (259, 3, '肺石风清|风行水上|半低不高|花朝月夜|一牛鸣地|蝉联往复', '清风半夜鸣蝉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (260, 3, '救命稻草|粲花之论|焚香列鼎|鞭辟着里|稗官小说|雪兆丰年|美意延年', '稻花香里说丰年');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (261, 3, '束手听命|强自取柱|臼灶生蛙|鹤唳风声|青山一发|根椽片瓦', '听取蛙声一片');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (262, 3, '七开八得|七穿八洞|个中之人|旗布星峙|恭行天罚|内修外攘', '七八个星天外');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (263, 3, '分星劈两|三瓦两舍|正点背画|箕风毕雨|寿山福海|快意当前', '两三点雨山前');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (264, 3, '整旧如新|岁时伏腊|草茅之臣|药店飞龙|稷蜂社鼠|林下风气|当耳边风', '旧时茅店社林边');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (265, 3, '云泥殊路|斗转参斜|溪壑无厌|科头跣足|玩忽职守|一偏之见', '路转溪头忽见');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (266, 3, '枯树生花|牵丝攀藤|三老五更|星桥火树|连昏达曙|字若涂鸦', '枯藤老树昏鸦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (267, 3, '辁才小慧|修桥补路|流脍人口|啜菽饮水|看人眉眼|治谱家传', '小桥流水人家');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (268, 3, '沉雄古逸|道旁苦李|东趋西步|惊才风逸|瘦童羸马|避世金马', '古道西风瘦马');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (269, 3, '魂飞目断|无肠可断|寸铁杀人|道在屎溺|大天白日|走遍天涯', '断肠人在天涯');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (270, 3, '曲学阿世|鼯之径|通天达地|曲径通幽|出处殊途', '曲径通幽处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (271, 3, '鬓丝禅榻|分房减口|耳热眼花|蠹众木折|深根蟠结', '禅房花木深');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (272, 3, '一彻万融|万籁俱静|不此之图|寝食俱废|万赖俱寂', '万籁此俱寂');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (273, 3, '唯唯绸否|博物洽闻|以莛叩钟|钟响磬鸣|鸾音鹤信', '唯闻钟磬音');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (274, 3, '岁不我与|浮生若寄|酒病花愁|降心相从|时不我与|识明智审|风清月朗', '我寄愁心与明月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (275, 3, '随时制宜|阑风长雨|例直禁简|神到之笔|衣锦夜游|何郎傅粉|东冲西决', '随风直到夜郎西');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (276, 3, '得君行道|问牛知马|众目所归|后会无期|得未曾有|动静有常|不期而遇', '君问归期未有期');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (277, 3, '巴高枝儿|范水模山|夙夜匪懈|飘风暴雨|潮涨潮落|多故之秋|肉林酒池', '巴山夜雨涨秋池');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (278, 3, '日饮亡何|所当无敌|两虎共斗|剪须和药|东驰西击|冷窗冻壁|炳烛夜游', '何当共剪西窗烛');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (279, 3, '却之不恭|话不投机|巴蛇吞象|丘山之功|夙夜匪解|雨]云u|与时偕行', '却话巴山夜雨时');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (280, 3, '烟视媚行|笼山络野|薄寒中人|马水车龙|日月重光|药笼中物|命染黄沙', '烟笼寒水月笼沙');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (281, 3, '月夜花朝|凤泊鸾漂|秦庭朗镜|逾淮之橘|近悦远来|金貂贳酒|破家县令', '夜泊秦淮近酒家');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (282, 3, '动如参商|女长须嫁|不轻然诺|知尽能索|取乱存亡|国耳忘家|相逢恨晚', '商女不知亡国恨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (283, 3, '天壤之隔|潘陆江海|犹鱼得水|唱沙作米|后拥前遮|分庭伉礼|柳啼花怨', '隔江犹唱后庭花');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (284, 3, '铅刀一割|聱牙诎曲|逐新趋异|遣词措意|酒食征逐|赏一劝百|杯水车薪', '一曲新词酒一杯');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (285, 3, '破壁飞去|五陵年少|补天柱地|嗳声叹气|弃旧怜新|新亭对泣|舞榭歌台', '去年天气旧亭台');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (286, 3, '晨兢夕厉|借尸还阳|东踅西倒|天下汹汹|审几度势|时绌举赢|回天再造', '夕阳西下几时回');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (287, 3, '慈明无双|不可揆度|无奈我何|莫敢谁何|粲花之论|襟怀洒落|拔葵去织', '无可奈何花落去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (288, 3, '似玉如花|曾几何时|宽猛相济|遗物识心|莺猜燕妒|一心同归|大来小往', '似曾相识燕归来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (289, 3, '大醇小疵|羊踏菜园|渡河香象|大相径庭|独胆英雄|观望徘徊|徘徊观望', '小园香径独徘徊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (290, 3, '守分安常|记问之学|溪壑无厌|四亭八当|长绳系日|幺豚暮鹨', '常记溪亭日暮');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (291, 3, '沉灶产蛙|醉眼蒙胧|百不一存|草木知威|燕燕于归|褴褛筚路', '沉醉不知归路');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (292, 3, '逸兴云飞|道尽涂殚|大器晚成|六趣轮回|破釜焚舟', '兴尽晚回舟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (293, 3, '不误农时|大含细入|藕断丝联|八花九裂|深根蟠结|详计审处', '误入藕花深处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (294, 3, '飞鸟惊蛇|鸿翔鸾起|劝百讽一|积沙成滩|闲鸥野鹭|鹭序鸳行', '惊起一滩鸥鹭');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (295, 3, '半筹不纳|畎亩下才|盂方水方|虎年成语|鹪鹩一枝|知人之鉴|晏开之警', '半亩方塘一鉴开');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (296, 3, '经纬天下|浮光略影|云合景从|质伛影曲|同文共规|徘徊观望|观望徘徊', '天光云影共徘徊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (297, 3, '聚米为谷|休休有容|有本有源|科头跣足|死声活气|流水高山|凤皇来仪', '为有源头活水来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (298, 3, '龙荒朔漠|弄性尚气|寄雁传书|金舌弊口|抱关击柝', '朔气传金柝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (299, 3, '唇竭齿寒|积厚流光|照萤映雪|入铁主簿|敝衣粝食', '寒光照铁衣');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (300, 3, '将相之器|临军对阵|百龄眉寿|战战栗栗|死声G气', '将军百战死');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (301, 3, '遒文壮节|江海之士|十夫Q椎|郑人争年|众毁所归', '壮士十年归');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (302, 3, '英雄短气|动如脱兔|展脚伸腰|望风扑影|扑朔迷离', '雄兔脚扑朔');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (303, 3, '孤雌寡鹤|玉兔银蟾|开眉展眼|迷头认影|流离播迁', '雌兔眼迷离');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (304, 3, '远至迩安|能近取譬|监貌辨色|先得我心|自是不彰|胆壮心雄|信口雌黄', '安能辨我是雄雌');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (305, 3, '齿牙春色|如潮涌至|带金佩紫|风栉雨沐|晚节不保|拔来报往|急吏缓民', '春潮带雨晚来急');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (306, 3, '漫天蔽野|普渡众生|布帆无恙|仓卒主人|焚舟破釜|头梢自领|横戈跃马', '野渡无人舟自横');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (307, 3, '九品中正|岂有是理|江汉朝宗|南航北骑|年谊世好|风前月下|急景凋年', '正是江南好风景');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (308, 3, '眼张失落|走花溜水|一时之标|砥砺风节|又红又专|遭逢会遇|民贵君轻', '落花时节又逢君');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (309, 3, '李广未封|杜门绝迹|诗以言志|鬼话连篇|逾千越万|口燥唇干|治谱家传', '李杜诗篇万口传');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (310, 3, '至心朝礼|当今无辈|喋喋不已|先觉先知|不即不离|独出新裁|活龙鲜健', '至今已觉不新鲜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (311, 3, '石烂江枯|表里山河|改姓易代|别有肺肠|公才公望|瘠人肥己|出于意表', '江山代有才人出');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (312, 3, '各司其事|振衣提领|风清月朗|迁客骚人|历历可数|百舌之声|尧年舜日', '各领风骚数百年');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (313, 3, '燕岱之石|覆宗绝嗣|I童钝夫|安堵如故|有何面目', '岱宗夫如何');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (314, 3, '戮力齐心|齐纨鲁缟|七青八黄|未艾方兴|不了而了', '齐鲁青未了');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (315, 3, '乡壁虚造|三年化碧|撞钟击鼓|神超形越|水明山秀', '造化钟神秀');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (316, 3, '阴服微行|阳九之厄|割骨疗亲|头昏脑闷|晓以大义', '阴阳割昏晓');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (317, 3, '拆家荡产|直抒胸臆|生齿日繁|曾无与二|云泥之别', '荡胸生曾云');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (318, 3, '迟徊不决|睚眦之怒|入孝出弟|异涂同归|鸟骇鼠窜', '决眦入归鸟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (319, 3, '能写会算|当刑而王|矜夸凌上|艳色绝世|顶头上司', '会当凌绝顶');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (320, 3, '日长一线|观山览水|众望攸归|山珍海错|辁才小慧', '一览众山小');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (321, 3, '国步方蹇|破瓜之年|关山迢递|暴虎冯河|风斯在下', '国破山河在');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (322, 3, '五言长城|如登春台|草偃风行|草木俱朽|深根固柢', '城春草木深');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (323, 3, '多情多感|时清海宴|梦笔生花|成语故事|泪迸肠绝', '感时花溅泪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (324, 3, '深仇积恨|目不别视|寒蝉僵鸟|匕鬯不惊|良工心苦', '恨别鸟惊心');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (325, 3, '烽火四起|不避汤火|引物连类|三战三北|风情月债', '烽火连三月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (326, 3, '家谕户晓|以书为御|将功抵罪|万象森罗|摆袖却金', '家书抵万金');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (327, 3, '舂容大雅|漠然置之|只影孤形|七孔生烟|正直无邪', '大漠孤烟直');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (328, 3, '长揖不拜|被山带河|襟怀洒落|晖光日新|面面俱圆', '长河落日圆');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (329, 3, '表里山河|随车夏雨|矜平躁释|鸣野食苹|裘弊金尽', '山随平野尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (330, 3, '江流日下|条入叶贯|大吹法螺|荒淫无度|流离琐尾', '江入大荒流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (331, 3, '青山一发|豆重榆瞑|马水车龙|苍黄翻复|迟疑不断|水米无交|世路荣枯', '山重水复疑无路');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (332, 3, '柳眉倒竖|暗斗明争|花朝月夜|连明达夜|损之又损|尝鼎一脔|村歌社鼓', '柳暗花明又一村');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (333, 3, '南面百城|川泽纳污|角巾东路|死到临头|海桑陵谷', '百川东到海');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (334, 3, '莫之谁何|风雨时若|不复堪命|西窗剪烛|全受全归', '何时复西归');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (335, 3, '安老怀少|马壮人强|横行不法|鼓眼努睛|殚谋戮力', '少壮不努力');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (336, 3, '携老挈幼|大败亏输|家徒四壁|伤化败俗|悲歌击筑', '老大徒伤悲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (337, 3, '攀今比昔|非异人任|严于律已|乘疑可间|黄钟长弃|槛花笼鹤|左来右去', '昔人已乘黄鹤去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (338, 3, '诸有此类|参天贰地|司空眼惯|读书三余|蝶粉蜂黄|猿鹤沙虫|玉楼赴召', '此地空余黄鹤楼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (339, 3, '绿衣黄里|不舞之鹤|青山一发|临去秋波|不分畛域|得而复失|返观内照', '黄鹤一去不复返');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (340, 3, '白衣秀士|穿云裂石|发引千钧|斗量车载|匹练飞空|悠闲自在|宛转悠扬', '白云千载空悠悠');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (341, 3, '日中必彗|途穷日暮|瘴乡恶土|九关虎豹|追悔何及|当轴处中|中心是悼', '日暮乡关何处是');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (342, 3, '七孔生烟|海不波溢|江翻海搅|附上罔下|矜才使气|加人一等|凤愁鸾怨', '烟波江上使人愁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (343, 3, '七青八黄|登山临水|横戈跃马|面北眉南|铁郭金城', '青山横北郭');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (344, 3, '白首北面|流水高山|珠围翠绕|东播西流|纸贵洛城', '白水绕东城');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (345, 3, '束身自爱|攀今掉古|逢机遘会|秋豪之末|悲观厌世|寂寂无闻|寥寥无几', '自古逢秋悲寂寥');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (346, 3, '舍我复谁|不言而谕|华屋秋墟|覆亡无日|聊胜一筹|皮里春秋|朝经暮史', '我言秋日胜春朝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (347, 3, '祈晴祷雨|群空冀北|百不一存|鹤唳华亭|排除万难|月地云阶|上陵下替', '晴空一鹤排云上');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (348, 3, '手头不便|以瞽引瞽|七步成诗|矫情饰貌|面面圆到|碧鬟红袖|气冲霄汉', '便引诗情到碧霄');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (349, 3, '花辰月夕|鲁阳指日|东兔西乌|下车伊始|雪窗萤几|敬授人时|愁肠九回', '夕阳西下几时回');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (350, 3, '称家有无|疾不可为|计出无奈|夫复何言|火烛银花|缕槔诼|番来覆去', '无可奈何花落去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (351, 3, '恩深似海|曾参杀人|降心相从|通元识微|燕妒莺惭|众望有归|往古来今', '似曾相识燕归来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (352, 3, '自用则小|梨园子弟|香轮宝骑|羊肠小径|独出新裁|观望徘徊|歧路徘徊', '小园香径独徘徊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (353, 3, '死而不僵|眠霜卧雪|孤恩负义|村歌社舞|蠖屈不伸|自崖而反|哀告宾服', '僵卧孤村不自哀');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (354, 3, '愁多夜长|玉砌雕阑|安枕而卧|周听不蔽|风前月下|吹叶嚼蕊|风雨晦冥', '夜阑卧听风吹雨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (355, 3, '铁网珊瑚|马鹿易形|伐冰之家|鼷鼠饮河|大含细入|梦撒寮丁|来者可追', '铁马冰河入梦来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (356, 3, '沉心静气|朽竹篙舟|侧足而立|畔道离经|彪炳千古|顺风扯帆|思过半矣', '沉舟侧畔千帆过');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (357, 3, '七病八倒|琼枝玉树|方丈盈前|两头白面|悬旌万里|斩木揭竿|丽藻春葩', '病树前头万木春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (358, 3, '折冲之臣|枪刀剑戟|沉湎淫逸|入海算沙|蕤宾铁响|素未谋面|目断魂销', '折戟沉沙铁未销');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (359, 3, '自拔来归|搴旗取将|砥砺琢磨|洗垢匿瑕|认死扣儿|前遮后拥|暮爨朝舂', '自将磨洗认前朝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (360, 3, '东捱西问|风行水上|不习水土|与古为徒|孔思周情|刘郎前度|悉听尊便', '东风不与周郎便');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (361, 3, '铜铸铁浇|雀马鱼龙|秋A春苗|深刺腧髓|一牛九锁|一齐二整|乔木世家', '铜雀春深锁二乔');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (362, 3, '拔地摇山|悬河注水|斧破秩|颠扑不碎|流风余韵|飘洋航海|兰因絮果', '山河破碎风飘絮');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (363, 3, '被发文身|饱谙世故|飞扬浮躁|沉潜刚克|对床夜雨|打情骂趣|风萍浪迹', '身世浮沉雨打萍');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (364, 3, '惶悚不安|恐后争先|暗礁险滩|人头畜鸣|街谈巷说|诚惶诚恐|有恃无恐', '惶恐滩头说惶恐');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (365, 3, '东零西碎|零丁孤苦|汪洋大海|千里同风|哀声叹气|七零八碎|一丁点儿', '零丁洋里叹零丁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (366, 3, '光可鉴人|门生故吏|自拔来归|攀今掉古|莫敢谁何|举无遗策|死亡无日', '人生自古谁无死');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (367, 3, '留中不下|仰取俯拾|一寸丹心|狠心辣手|心照情交|功劳汗马|青史传名', '留取丹心照汗青');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (368, 3, '词约指明|壶中日月|沉几观变|与时俯仰|动静有常', '明月几时有');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (369, 3, '把素持斋|酒食征逐|追踪问迹|纡青佩紫|炼石补天', '把酒问青天');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (370, 3, '百不当一|困知勉行|楚天云雨|腥闻在上|一片宫商|慢弛之阙', '不知天上宫阙');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (371, 3, '今昔之感|昼警夕惕|讨是寻非|何所不有|惨绿年华', '今夕是何年');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (372, 3, '我黼子佩|欲取姑与|乘隙而入|流风余韵|捆载而归|去而之他', '我欲乘风归去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (373, 3, '损之又损|争前恐后|瑶林琼树|画阁朱楼|金玉锦绣|雕墙峻宇', '又恐琼楼玉宇');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (374, 3, '高材疾足|出处殊涂|百世不磨|如不胜衣|送暖偷寒', '高处不胜寒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (375, 3, '起凤腾蛟|妙舞清歌|弱不好弄|笑比河清|星流影集', '起舞弄清影');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (376, 3, '追悔何及|似箭在弦|智藏在|瘠己肥人|间不容息', '何似在人间');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (377, 3, '瘠己肥人|得未曾有|悲天悯人|诚欢诚喜|流离播越|天缘凑合', '人有悲欢离合');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (378, 3, '不日不月|奥援有灵|阴阳交错|乍雨乍晴|凿圆枘方|花残月缺', '月有阴晴圆缺');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (379, 3, '彼倡此和|乐事劝功|以古非今|临难铸兵|全身远害', '此事古难全');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (380, 3, '但愿如此|天遂人愿|觉人觉世|校短量长|久战沙场', '但愿人长久');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (381, 3, '不揪不采|春兰秋菊|推东主西|依人篱下|依人篱下', '采菊东篱下');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (382, 3, '谬悠之说|惠然之顾|见性成佛|地北天南|背山起楼', '悠然见南山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (383, 3, '升山采珠|矜功负气|壶中日月|朝荣夕悴|伫候佳音', '山气日夕佳');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (384, 3, '鸦飞雀乱|鸟道羊肠|盎盂相击|欲取姑与|一还一报', '飞鸟相与还');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (385, 3, '在此存照|釜中生鱼|取予有节|丁真楷草|出自意外', '此中有真意');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (386, 3, '上下同欲|薰莸无辨|恶贯已盈|蓼虫忘辛|询事考言', '欲辨已忘言');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (387, 3, '金科玉条|樽前月下|神清气茂|金貂取酒|斗折蛇行|三十三天|渭川千亩', '金樽清酒斗十千');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (388, 3, '金科玉条|解衣盘礴|什袭珍藏|羞愧难当|忠果正直|一彻万融|捉鬼卖钱', '玉盘珍羞直万钱');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (389, 3, '死欲速朽|暗渡陈仓|蝶粉蜂黄|悬河注水|戛玉敲冰|壶浆塞道|川泽纳污', '欲渡黄河冰塞川');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (390, 3, '鸿鹄将至|一谷不登|太阿倒持|困知勉行|报雠雪恨|不自满假|肉山脯林', '将登太行雪满山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (391, 3, '荡检逾闲|往蹇来连|青史垂名|饵名钓禄|金马碧鸡|綦溪利|上替下陵', '闲来垂钓碧溪上');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (392, 3, '瞻前忽后|反吟复吟|乘间伺隙|溯水行舟|黄梁美梦|干云蔽日|苦海无边', '忽复乘舟梦日边');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (393, 3, '钜人长德|风行水上|冰解的破|浪子宰相|阳九之会|赖有此耳|动不失时', '长风破浪会有时');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (394, 3, '举直错枉|披红挂彩|风起云蒸|顺风张帆|合衷共济|沧海一鳞|群鸿戏海', '直挂云帆济沧海');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (395, 3, '北窗高卧|惊才风逸|卷帙浩繁|四战之地|白丁俗客|白草黄云|瓶沉簪折', '北风卷地白草折');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (396, 3, '胡言乱道|黑天摸地|四至八道|抹月批风|即温听厉|破壁飞去|冰肌雪肤', '胡天八月即飞雪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (397, 3, '积于忽微|如坐春风|加人一等|晨兴夜寐|如坐春风|北风之恋|浮来暂去', '忽如一夜春风来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (398, 3, '千龄万代|拔树搜根|惊惶万状|火树琪花|梨园子弟|路柳墙花|开物成务', '千树万树梨花开');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (399, 3, '剑树刀山|挥戈回日|戳无路儿|扭转干坤|不次之位|见兔放鹰|逢君之恶', '山回路转不见君');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (400, 3, '冰瓯雪|上援下推|杼柚其空|中留不报|匹马只轮|素隐行怪|不遑宁处', '雪上空留马行处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (401, 3, '落月屋梁|张红燃爆|百不获一|吞舟是漏|无能为役|情天孽海|开物成务', '落红不是无情物');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (402, 3, '执而不化|作浪兴风|春冰虎尾|云泥之差|更长漏永|官官相护|簇锦团花', '化作春泥更护花');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (403, 3, '独坐愁城|心在魏阙|相辅而行|三曹对案|肝胆秦越', '城阙辅三秦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (404, 3, '风清月朗|七孔生烟|道相望|五类杂种|津津有味', '风烟望五津');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (405, 3, '吾谁与归|君命无二|绝俗离世|别居异财|刻意求工', '与君离别意');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (406, 3, '河同水密|貌是心非|宦囊羞涩|衣锦昼游|金针度人', '同是宦游人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (407, 3, '蜃楼海市|彻内彻外|死生存亡|鲍子知我|罪己责躬', '海内存知己');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (408, 3, '补天浴日|咫尺天涯|印累绶若|连类比物|邻里乡党', '天涯若比邻');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (409, 3, '无毁无誉|反劳为逸|恫在身|歧出歧入|末路之难', '无为在歧路');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (410, 3, '儿女之债|倩女离魂|同舟共命|学不沾洽|纶巾羽扇', '儿女共沾巾');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (411, 3, '踟躇不前|不绝如发|海枯见底|古来今往|金人三缄', '前不见古人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (412, 3, '鸡口牛后|不轻然诺|见素抱朴|凤皇来仪|观者如垛', '后不见来者');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (413, 3, '恤孤念苦|地平天成|拔地摇山|藏之名山|悠然自得|悠悠荡荡', '念天地之悠悠');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (414, 3, '独得之见|凄怆流涕|愀然不乐|绝裾而去|感激流涕|上下其手', '独怆然而涕下');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (415, 3, '迁客骚人|白云亲舍|白饭青刍|功标青史|柳街花巷|惊愕失色|标新领异', '客舍青青柳色新');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (416, 3, '劝善黜恶|志诚君子|半夜三更|词穷理尽|同功一体|杯圈之思|斗酒只鸡', '劝君更尽一杯酒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (417, 3, '东冲西决|旁见侧出|阳九百六|闭关自主|无能为役|自我作故|斗筲之人', '西出阳关无故人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (418, 3, '日长一线|风栉雨沐|五鼎万钟|悬旌万里|金风送爽|华星秋月|雕心雁爪', '长风万里送秋雁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (419, 3, '新亭对泣|在此存照|无际可寻|以辞害意|酒酣耳熟|材高知深|楼阁台榭', '对此可以酣高楼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (420, 3, '飘蓬断梗|悬圃蓬莱|断发文身|稀句绘章|运筹建策|安车蒲轮|泽及枯骨', '蓬莱文章建安骨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (421, 3, '曳尾涂中|濮上桑间|予末小子|檀郎谢女|损之又损|冰洁渊清|不绝如发', '中间小谢又清发');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (422, 3, '人琴俱亡|怀金垂紫|逸趣横生|兴致索然|龙神马壮|杯圈之思|飞觞走', '俱怀逸兴壮思飞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (423, 3, '欲盖而彰|附上罔下|名标青史|龚行天罚|独揽大权|大明法度|无边风月', '欲上青天揽明月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (424, 3, '抽简禄马|心如刀搅|断发文身|登山临水|盂方水方|更令明号|流水桃花', '抽刀断水水更流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (425, 3, '百废俱举|杯弓市虎|东窗消息|玉惨花愁|破愁为笑|更长梦短|万恨千愁', '举杯消愁愁更愁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (426, 3, '爱人利物|生发未燥|念兹在兹|惑世盗名|苗而不秀|啧啧称羡|美意延年', '人生在世不称意');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (427, 3, '考绩幽明|花朝月夜|雾散云披|白发朱颜|顾影弄姿|轮扁斫轮|借水行舟', '明朝散发弄扁舟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (428, 3, '天壤王郎|柳陌花街|臧仓小人|袂云汗雨|搜肠润吻|恍如梦寐|滴粉搓酥', '天街小雨润如酥');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (429, 3, '草偃风从|室怒市色|遥遥无期|拄笏看山|相亲相近|却老还童|兼爱无私', '草色遥看近却无');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (430, 3, '不耻最后|人非物是|桂林一枝|美意延年|鸿爪春泥|年谊世好|东墙处子', '最是一年春好处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (431, 3, '奔逸绝尘|罪不胜诛|烟销灰灭|柳街花巷|器满意得|黼黻皇猷|都鄙有章', '绝胜烟柳满皇都');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (432, 3, '以沫相濡|寡见鲜闻|遭际时会|一言难罄|分别门户|教亦多术|解人难得', '相见时难别亦难');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (433, 3, '东海扬尘|改俗迁风|贵贱无二|爱日惜力|百舍重茧|火烛银花|杯残炙冷', '东风无力百花残');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (434, 3, '春晖寸草|蚕丝牛毛|恰到好处|死告活央|哀丝豪竹|矩步方行|成风尽垩', '春蚕到死丝方尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (435, 3, '味同嚼蜡|付诸一炬|运斤成风|心若死灰|泪迸肠绝|发轫之始|干戈载戢', '蜡炬成灰泪始干');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (436, 3, '连更晓夜|秦镜高悬|但愿如此|愁长酒|云散风流|颜丹鬓绿|更弦改辙', '晓镜但愁云鬓改');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (437, 3, '夙夜匪解|寻绎吟玩|鸣鹤之应|先知先觉|咏月嘲风|争光日月|饥寒交凑', '夜吟应觉月光寒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (438, 3, '席门蓬巷|排山压卵|至于此极|去杀胜残|安忍无亲|勇动多怨|荜路蓝蒌', '蓬山此去无多路');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (439, 3, '俯拾青紫|鸟伏兽穷|青鸟殷勤|勤慎肃恭|且住为佳|探观止矣|看碧成朱', '青鸟殷勤为探看');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (440, 3, '家无儋石|交浅言深|独步一时|冠上加冠|西窗剪烛|珍楼宝屋', '无言独上西楼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (441, 3, '半间不界|畏天知命|浮收勒索|云泥殊路|东遮西掩|守望相助|眼腰黄赤', '不畏浮云遮望眼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (442, 3, '只鸾单凤|金玉良缘|一身两役|心在魏阙|为善最乐|高枕安卧|层楼叠榭', '只缘身在最高层');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (443, 3, '老天拔地|三夫之言|穷极无聊|生发未燥|沉重少言|同年而校|惊喜若狂', '老夫聊发少年狂');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (444, 3, '无其奈何|挥戈回日|命辞遣意|冯生弹铗|云散高唐', '何日遣冯唐');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (445, 3, '千载一合|匕鬯不惊|目动言肆|许由洗耳|兵多者败|云愁雨怨', '载不动许多愁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (446, 3, '利时及物|口是心苗|盗憎主人|非异人任|言中事隐|事款则圆|喋喋不休', '物是人非事事休');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (447, 3, '欲益反弊|琵琶旧语|哭天抹泪|拥彗先驱|流风回雪', '欲语泪先流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (448, 3, '越扶越醉|鞭辟着里|目挑眉语|黑灯瞎火|看朱成碧|_目案剑', '醉里挑灯看剑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (449, 3, '魂颠梦倒|旋复回皇|一吹一唱|咫角骖驹|连更星夜|安营下寨', '梦回吹角连营');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (450, 3, '四衢八街|百废具兴|鞭辟入里|三分鼎足|拱揖指麾|折节下士|风吹日炙', '八百里分麾下炙');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (451, 3, '去天尺五|目下十行|易辙改弦|翻山越水|无以塞责|弦外之响|丑声远播', '五十弦翻塞外声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (452, 3, '披沙剖璞|袍笏登场|春蛙秋蝉|朱衣点头|谷马砺兵', '沙场秋点兵');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (453, 3, '马鹿易形|拿班作势|一语破的|掩口胡卢|水尽鹅飞|称心快意', '马作的卢飞快');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (454, 3, '盘马弯弓|风雨如磐|晴天霹雳|晴天霹雳|楚管蛮弦|边尘不惊', '弓如霹雳弦惊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (455, 3, '明白了当|却行求前|岂弟君子|图王霸业|黑天摸地|上好下甚|事不师古', '了却君王天下事');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (456, 3, '时诎举赢|佐饔得尝|十死一生|通前澈后|灰身泯智|通前澈后|惑世盗名', '赢得生前身后名');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (457, 3, '不可揆度|惜老怜贫|白龙鱼服|披发左衽|安生乐业', '可怜白发生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (458, 3, '孝悌力田|打家截舍|裙屐少年|荡检逾闲|水月观音', '田家少闲月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (459, 3, '挨三顶五|冰壶秋月|爱人利物|声价倍增|意乱心忙', '五月人倍忙');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (460, 3, '妇人之仁|姑置勿论|感恩荷德|箪食豆羹|鹑居d食', '妇姑荷箪食');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (461, 3, '童牛角马|扶老挟稚|携家带口|挈}提壶|屠狗卖浆', '童稚携壶浆');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (462, 3, '月夕花晨|此伏彼起|动中肯綮|能征惯战|悬建铎', '晨起动征铎');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (463, 3, '泉下之客|乘伪行诈|茹泣吞悲|蜕故孳新|离乡背井', '客行悲故乡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (464, 3, '鸡犬桑麻|浮声切响|分茅列土|药店飞龙|日月交食', '鸡声茅店月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (465, 3, '慧业文人|避迹违心|铁板一块|据水断桥|戴霜履冰', '人迹板桥霜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (466, 3, '家无儋石|情投意|攻苦食淡|争锋吃醋|春风和气', '无意苦争春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (467, 3, '人己一视|使贤任能|群鸿戏海|兰桂腾芳|妒富愧贫', '一任群芳妒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (468, 3, '只鸡樽酒|罗敷有夫|焚香顶礼|如是我闻|故宫禾黍', '只有香如故');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (469, 3, '贻燕之训|予末小子|大来小往|动不失时|文君新寡|社鼠城狐', '燕子来时新社');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (470, 3, '梨花带雨|流水桃花|碧落黄泉|前危后则|冬温夏清|黜幽陟明', '梨花落后清明');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (471, 3, '榷酒征茶|颠连穷困|扬镳分路|善善从长|授职惟贤|欲谁归罪|睡眼朦胧', '酒困路长惟欲睡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (472, 3, '爱日惜力|高掌远|户告人晓|援溺振渴|漫诞不稽|思过半矣|闲茶浪酒', '日高人渴漫思茶');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (473, 3, '斗筲之人|比肩叠迹|黄人守日|簇锦团花|面黄肌瘦', '人比黄花瘦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (474, 3, '人莫予毒|枉道事人|不当不正|骨瘦形销|遗魂亡魄', '莫道不销魂');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (475, 3, '有死无二|暗室亏心|焚香列鼎|车马盈门|袖里乾坤', '有暗香盈袖');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (476, 3, '寸心千古|泥古执今|张脉偾兴|追亡逐北|多历年稔|绝甘分少|比类从事', '千古兴亡多少事');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (477, 3, '生公说法|扫眉才子|所当无敌|如蹈汤火|公子王孙|不分伯仲|询谋佥同', '生子当如孙仲谋');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (478, 3, '沉烽静柝|泼油救火|照萤映雪|东趋西步|五日京兆', '烽火照西京');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (479, 3, '灰心槁形|中彪外|自拔来归|匕鬯不惊|平心易气', '心中自不平');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (480, 3, '咨牙g嘴|弄璋之庆|异口同辞|吕安题凤|心在魏阙', '牙璋辞凤阙');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (481, 3, '伸钩索铁|骑虎难下|龙蟠虎绕|云龙井蛙|日坐愁城', '铁骑绕龙城');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (482, 3, '冰天雪窖|明珠投暗|松柏后凋|重张旗鼓|胰艋一', '雪暗凋旗画');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (483, 3, '浇风薄俗|丰筋多力|刮刮杂杂|钟鼓馔玉|谠言直声', '风多杂鼓声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (484, 3, '深根宁极|师直为壮|劝百讽一|灌夫骂坐|校短量长', '宁为百夫长');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (485, 3, '乘利席胜|拿班作势|铅刀一割|牛角挂书|嘘枯吹生', '胜作一书生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (486, 3, '槛花笼鹤|蹄间三寻|剑头一|扣壶长吟|只鸡樽酒', '花间一壶酒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (487, 3, '独出新裁|酌古斟今|案无留牍|楚囚相对|指亲托故', '独酌无相亲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (488, 3, '束举火|杯酒言欢|邀名射利|白蜡明经|风情月债', '举杯邀明月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (489, 3, '衾影独对|捉风捕影|持盈守成|九烈三贞|夹袋人物', '对影成三人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (490, 3, '语出月胁|东曦既驾|苗而不秀|解衣般礴|啜菽饮水', '月既不解饮');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (491, 3, '捉影追风|徒劳往返|随珠弹雀|生我劬劳|慎身修永', '影徒随我身');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (492, 3, '暂劳永逸|伴食中书|月旦春秋|大命将泛|避影匿形', '暂伴月将影');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (493, 3, '砥行磨名|礼崩乐坏|须眉男子|剑及屦及|有脚阳春', '行乐须及春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (494, 3, '助我张目|楚舞吴歌|日月丽天|歧路徘徊|徘徊不定', '我歌月徘徊');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (495, 3, '舍我复谁|鸾飞凤舞|迷头认影|零落山丘|妄言则乱', '我舞影零乱');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (496, 3, '半梦半醒|遭时定制|同然一辞|凤友鸾交|追欢买笑', '醒时同交欢');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (497, 3, '烂醉如泥|前歌后舞|各行其是|谛分审布|冰解云散', '醉后各分散');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (498, 3, '一劳永逸|百结悬鹑|汗颜无地|深情底理|优游不断', '永结无情游');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (499, 3, '同美相妒|幽期密约|旷邈无家|子曰诗云|铮铮铁汉', '相期邈云汉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (500, 3, '出群拔萃|鸡尸牛从|正己守道|祸乱滔天|喊冤叫屈', '群鸡正乱叫');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (501, 3, '借客报仇|通首至尾|炙鸡絮酒|卵与石斗|争权攘利', '客至鸡斗争');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (502, 3, '驱霆策电|鸡争鹅斗|从壁上观|暮云春树|亡猿灾木', '驱鸡上树木');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (503, 3, '有始有卒|兵闻拙速|叩角商歌|柴车幅巾|荆衡杞梓', '始闻叩柴荆');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (504, 3, '梦笔生花|悦近来远|高蹈远举|朱阁青楼|触景伤心|杜门谢客|目挑心招', '花近高楼伤客心');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (505, 3, '明见万里|凫鹤从方|勇动多怨|执经问难|此呼彼应|登山小鲁|临潼斗宝', '万方多难此登临');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (506, 3, '玉食锦衣|倒海翻江|触手生春|色衰爱寝|开来继往|恭行天罚|立地书厨', '锦江春色来天地');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (507, 3, '乱琼碎玉|顽固堡垒|浮收勒索|浮云富贵|通时合变|遗风古道|酌古斟今', '玉垒浮云变古今');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (508, 3, '辅车相将|大树将军|浑金璞玉|韬戈卷甲|弥日累夜|不世之功|通脱不拘', '将军金甲夜不脱');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (509, 3, '鹤知夜半|夜以接日|常胜将军|风掣雷行|枕戈刃|漠不相关|拨嘴撩牙', '半夜军行戈相拨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (510, 3, '风云月露|垢面蓬头|官法如炉|吞刀吐火|面壁功深|机变如神|心如刀割', '风头如刀面如割');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (511, 3, '尝鼎一脔|鱼封雁帖|朝闻夕死|野人奏曝|九仞一篑|重足而立|际地蟠天', '一封朝奏九重天');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (512, 3, '寅忧夕惕|示贬于褒|心血来潮|州如斗大|迷踪失路|七捞八攘|彪炳千古', '夕贬潮州路八千');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (513, 3, '甘苦与共|看生见长|更令明号|弄月嘲风|此动彼应|宵鱼垂化|一字一泪', '共看明月应垂泪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (514, 3, '太仓一粟|连明达夜|人离乡贱|锦心绣腹|拔十得五|夙兴夜处|公诸同好', '一夜乡心五处同');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (515, 3, '黑漆皮灯|春树暮云|压肩叠背|成城断金|日坐愁城|清心少欲|摧枯振朽', '黑云压城城欲摧');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (516, 3, '束戈卷甲|潜光隐德|向声背实|补天浴日|戛玉敲金|附翼攀鳞|禁舍开塞', '甲光向日金鳞开');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (517, 3, '总角之交|谬采虚声|七满八平|大天白日|洞察秋毫|神色不挠|鞭擗进里', '角声满天秋色里');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (518, 3, '下塞上聋|附上罔下|燕俦莺侣|民脂民膏|阴凝冰坚|以夜继日|俯拾青紫', '塞上燕脂凝夜紫');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (519, 3, '半痴不颠|旌旗卷舒|惨绿愁红|卧旗息鼓|临风对月|安危相易|流水桃花', '半卷红旗临易水');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (520, 3, '凛如霜雪|重关击柝|补卧鼓|寒耕热耘|飞声腾实|不次之位|孝经起序', '霜重鼓寒声不起');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (521, 3, '春祈秋报|文君早寡|黄冠草服|金城汤池|金台市骏|桑间濮上|先意承旨', '报君黄金台上意');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (522, 3, '提纲振领|携老挈幼|伯玉知非|药店飞龙|摧刚为柔|志诚君子|死亡枕藉', '提携玉龙为君死');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (523, 3, '悲歌易水|独是独非|正法眼藏|趁波逐浪|排S纵横', '水是眼波横');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (524, 3, '积甲山齐|作如是观|眉目如画|千峰万壑|兽聚鸟散', '山是眉峰聚');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (525, 4, '万目睽睽|物连类|凛若冰霜|经纬天下|争多竞少|山木自寇|听命由天', '万类霜天竞自由');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (526, 4, '寻山问水|苍黄翻复|苦海茫茫|大展经纶|立地书橱', '问苍茫大地');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (527, 4, '恰中要害|同恶相党|道山学海|反老为少|年丰岁稔', '恰同学少年');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (528, 4, '佛头著粪|土壤细流|当着不着|三年化碧|知一万毕|穿房入户|拜将封侯', '粪土当年万户侯');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (529, 4, '祥麟威凤|凤凰在@|阆苑瑶台|濠梁之上|丹凤朝阳|景星凤凰|马如游龙', '凤凰台上凤凰游');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (530, 4, '跨凤乘龙|去天尺五|近水楼台|匏瓜空悬|相忘江湖|请自隗始|流水桃花', '凤去台空江自流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (531, 4, '总角之好|橘化为枳|浮一大白|穿云裂石|力能扛鼎|隐天蔽日|壶中日月', '总为浮云能蔽日');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (532, 4, '遁世长往|长安棋局|苗而不秀|尸居龙见|称贤使能|蒹葭伊人|酒病花愁', '长安不见使人愁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (533, 4, '朱衣点额|弦外遗音|孜孜不已|且住为佳|渐至佳境|稠人广坐|绝世独立', '朱弦已为佳人绝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (534, 4, '皓齿青蛾|嵬眼苟|聊以自慰|因势乘便|天不作美|乞浆得酒|横眉瞪目', '青眼聊因美酒横');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (535, 4, '万象森罗|皮里春秋|归马放牛|楼船箫鼓|弄口鸣舌|校短量长|虎年成语', '万里归船弄长笛');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (536, 4, '耿耿此心|刿目怵心|以尽吾齿|命与仇谋|白旄黄钺|鸥鹭忘机|松柏寒盟', '此心吾与白鸥盟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (537, 4, '千载一弹|重岩迭障|经纬万端|飞砂转石|不讳之路|不分畛域|心安神定', '千岩万转路不定');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (538, 4, '遂迷忘反|柳啼花怨|祸福相倚|漱石枕流|恍恍忽忽|孜孜不已|风雨晦暝', '迷花倚石忽已暝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (539, 4, '白青衫|风雨晦冥|浩如烟海|东扬西荡|敖不可长|见危授命|方底圆盖', '青冥浩荡不见底');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (540, 4, '皎如日星|月旦春秋|照猫画虎|扬威耀武|金浆玉醴|怀银纡紫|黄台之瓜', '日月照耀金银台');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (541, 4, '炙冰使燥|先得我心|百世不磨|先得我心|开基创业|摧心剖肝|靡颜腻理', '使我不得开心颜');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (542, 4, '苟且偷生|放诞不拘|抽黄对白|中原逐鹿|朱阁青楼|勒马悬崖|间不容发', '且放白鹿青崖间');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (543, 4, '休明盛世|濠濮间想|锦衣行昼|礼乐崩坏|人云亦云|眉目如画|诸若此类', '世间行乐亦如此');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (544, 4, '今月古月|沓来踵至|一波万波|言听事行|东观之殃|枕流漱石|海水群飞', '古来万事东流水');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (545, 4, '惊喜欲狂|投膏止火|光可鉴人|邈处K视|痛湔宿垢', '欲投人处宿');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (546, 4, '隔壁撺椽|盘水加剑|十死不问|樵柯烂尽|愚夫愚妇', '隔水问樵夫');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (547, 4, '拖紫垂青|拔山超海|心长绠短|风云月露|暗室亏心|冰瓯雪|登山小鲁', '青海长云暗雪山');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (548, 4, '孤标独步|略地侵城|遥相应和|道相望|龟玉毁椟|闭门扫轨|闭户关门', '孤城遥望玉门关');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (549, 4, '黄发骀背|排沙见金|劝百讽一|连战皆捷|椎理穿掘|错彩镂金|坚甲利兵', '黄沙百战穿金甲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (550, 4, '不根之谈|吹弹得破|龙楼凤城|兰艾同焚|善始令终|不习水土|还年s老', '不破楼兰终不还');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (551, 4, '丁一确二|骑马找马|艳紫妖红|风尘之会|虎年成语|膏粱子弟|语笑喧哗', '一骑红尘妃子笑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (552, 4, '多闻阙疑|指事类情|弊帚自珍|超今冠古|伤风败化|星离雨散|别出手眼', '多情自古伤离别');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (553, 4, '被坚执锐|心闲手敏|降心相从|看风驶篷|哭天抹泪|铺眉蒙眼', '执手相看泪眼');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (554, 4, '有志竟成|全无心肝|琵琶旧语|屏气凝神|祝哽祝噎', '竟无语凝噎');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (555, 4, '自今已往|宵寝晨兴|金龟换酒|一喷一醒|亡可奈何|锥处囊中', '今宵酒醒何处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (556, 4, '梧鼠五技|破桐之叶|半夜三更|兼收并畜|绵言细语|云尤雨', '梧桐更兼细雨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (557, 4, '停云落月|迷花沾草|风行水上|口齿生香|土阶茅茨|舍己芸人|晚节不保', '落花水香茅舍晚');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (558, 4, '暗弱无断|据水断桥|垂头翼|买犊卖刀|及宾有鱼|蒹葭伊人|休牛散马', '断桥头卖鱼人散');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (559, 4, '款学寡闻|伯道无儿|前古未有|先难后获|曙后星孤', '闻道有先后');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (560, 4, '教亦多术|乐业安居|称家有无|专权恣肆|宵旰攻苦', '术业有专攻');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (561, 4, '弄玉偷香|黄风雾罩|风云月露|雪鬓霜鬟|干啼湿哭', '香雾云鬟湿');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (562, 4, '天清日白|珠璧联辉|金科玉条|螳臂当辕|送暖偷寒', '清辉玉臂寒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (563, 4, '如之奈何|遵时养晦|倚天拔地|虚左以待|成语谜语', '何时倚虚幌');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (564, 4, '一雕双兔|囊萤照雪|哭眼抹泪|刮垢磨痕|干名犯义', '双照泪痕干');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (565, 4, '主敬存诚|户告人晓|屋下架屋|弓调马服|孤悬客寄|珊瑚在网|楼船箫鼓', '主人下马客在船');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (566, 4, '毛举细故|浆酒霍肉|恣心纵欲|饮冰茹蘖|即鹿无虞|拈毫弄管|琴断朱弦', '举酒欲饮无管弦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (567, 4, '忧心若醉|不愧下学|养虺成蛇|欢喜冤家|惨遭不幸|将夺固与|琵琶别抱', '醉不成欢惨将别');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (568, 4, '离乡别土|济世匡时|茫无所知|茫无头绪|江淹才尽|浸明浸昌|跳丸日月', '别时茫茫江浸月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (569, 4, '玩忽职守|直谅多闻|廉泉让水|从壁上观|琵琶胡语|铁绰铜琶|下气怡声', '忽闻水上琵琶声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (570, 4, '主贤臣良|光可鉴人|不忘久要|振旅而归|迁客骚人|不直一钱|诗书发冢', '主人忘归客不发');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (571, 4, '彪炳千古|呼天号地|万目睚眦|千唤万唤|洒心更始|出奴入主|有凤来仪', '千呼万唤始出来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (572, 4, '犹豫不定|抱枝拾叶|琵琶别抱|铁板铜琶|减师半德|遮天映日|白面儒生', '犹抱琵琶半遮面');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (573, 4, '柔肠百转|牙签犀轴|拨乱返正|铁板铜弦|三瓦两舍|两叶掩目|偷声细气', '转轴拨弦三两声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (574, 4, '谦让未遑|习与性成|泛应曲当|调虎离窠|责先利后|及宾有鱼|睹物兴情', '未成曲调先有情');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (575, 4, '别籍异财|有凤来仪|寻幽探胜|绿惨红愁|明推暗就|旧愁新恨|臼灶生蛙', '别有幽愁暗恨生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (576, 4, '何至于此|玩日允|即鹿无虞|声振林木|不可胜算|有志竟成|休声美誉', '此时无声胜有声');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (577, 4, '铁画银钩|一钵一瓶|乍暖还寒|破柱求奸|悬河注水|拖浆带水|泪迸肠绝', '银瓶乍破水浆迸');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (578, 4, '寸兵尺铁|结驷列骑|左冲右突|六出纷飞|刀枪剑戟|匕首投枪|河清社鸣', '铁骑突出刀枪鸣');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (579, 4, '唯唯否否|秋扇见捐|陆海潘江|腹心相照|华星秋月|月露风云|富埒陶白', '唯见江心秋月白');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (580, 4, '十鼠同穴|播弄是非|恭行天罚|愁海无涯|浑沦吞枣|桑落瓦解|非异人任', '同是天涯沦落人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (581, 4, '道相属|逢场作趣|今夕何夕|登高必赋|曾参杀人|相去无几|见经识经', '相逢何必曾相识');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (582, 4, '俯拾仰取|n首信眉|肥甘轻暖|无能为役|妇人孺子|之子于归|敲牛宰马', '俯首甘为孺子牛');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (583, 4, '横眉冷目|看人眉睫|残羹冷炙|夜雨对床|千里犹面|夫复何言|词约指明', '横眉冷对千夫指');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (584, 4, '拭面容言|呵壁问天|金钗十二|年谊世好|应图受|美男破老|隔壁撺椽', '面壁十年图破壁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (585, 4, '河清难俟|一献三酬|如蹈水火|海枯见底|不亦乐乎|英声茂实|心雄万夫', '难酬蹈海亦英雄');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (586, 4, '加人一等|乘疑可间|公明正大|道旁苦李|论列是非|陵谷沧桑|桑田沧海', '人间正道是沧桑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (587, 4, '胡天胡帝|辨若悬河|亢龙有悔|遣兴陶情|拔地倚天|亦步亦趋|老而不死', '天若有情天亦老');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (588, 4, '雄伟壮观|参透机关|漫天彻地|称雨道晴|真山真水|机变如神|伸钩索铁', '雄关漫道真如铁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (589, 4, '避世绝俗|轶闻遗事|空洞无物|公明正大|触目皆是|学问思辨|含蓼问疾', '世事洞明皆学问');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (590, 4, '夹袋人物|情逾骨肉|饱练世故|四冲六达|若即若离|烦文缛礼|走马章台', '人情练达即文章');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (591, 4, '打勤献趣|了身达命|甘言巧辞|弹剑作歌|五鼎万钟|矢死不二|众怨之的', '献身甘作万矢的');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (592, 4, '蝇营狗苟|利市三倍|专权误国|称家有无|虎啸风生|以规为|昧死以闻', '苟利国家生以死');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (593, 4, '去天尺五|养虎留患|摅肝沥胆|析肝吐胆|三好两歉|金友玉昆|鹘仑吞枣', '去留肝胆两昆仑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (594, 4, '实获我心|洁清自矢|横眉瞪目|枪林刀树|风向草偃|楚天云雨|言笑嘻怡', '我自横刀向天笑');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (595, 4, '吹垢索瘢|南面百城|百不当一|冷面寒铁|杨柳依依|柳腰花态|改俗迁风', '吹面不寒杨柳风');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (596, 4, '学不沾洽|缝衣浅带|清心少欲|偎干就湿|杏脸桃腮|流水桃花|拨雨撩云', '沾衣欲湿杏花雨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (597, 4, '学不躐等|闲话休题|爱口识羞|疾足先得|河东三箧|风清弊绝|富面百城', '等闲识得东风面');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (598, 4, '计出万全|以紫为朱|运筹千里|绿惨红愁|总总林林|公是公非|春树暮云', '万紫千红总是春');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (599, 4, '乞浆得酒|送往劳来|才貌兼全|不可胜计|浪费笔墨|功薄蝉翼|贩夫驺卒', '得来全不费功夫');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (600, 4, '踏步不前|美男破老|点金作铁|磨穿铁鞋|伯道无儿|追踪觅影|居轴处中', '踏破铁鞋无觅处');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (601, 4, '片纸只字|从壁上观|读书得间|沓来踵至|慎终若始|觉人觉世|学浅才疏', '纸上得来终觉浅');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (602, 4, '音问两绝|无知妄作|彼唱此和|名山事业|不忘久要|躬逢盛事|行同狗彘', '绝知此事要躬行');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (603, 4, '骥子龙文|雕章绘句|掂梢折本|三十三天|贪墨成风', '文章本天成');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (604, 4, '妙手空空|革面敛手|遭时不偶|凶竖得志|北风之恋', '妙手偶得之');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (605, 4, '望梅止渴|磨砺以须|傲慢不逊|流风回雪|三三五五|分香卖履|抽黄对白', '梅须逊雪三分白');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (606, 4, '雪窖冰天|却病延年|助边输财|香药脆梅|举一废百|碎尸万段|撮土焚香', '雪却输梅一段香');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (607, 4, '高谭清论|代马依风|半三不四|朝令夜遁|钟鸣鼎列|寒蝉僵鸟', '清风半夜鸣蝉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (608, 4, '众人国士|瓮里醯鸡|讨是寻非|他山之攻|百品千条|九流百家|普度群生', '众里寻他千百度');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (609, 4, '粉白黛绿|穿杨贯虱|烟霏雾集|化外之民|老不晓事|心寒胆落|群轻折轴', '绿杨烟外晓寒轻');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (610, 4, '软红香土|杏腮桃颊|枝附叶著|容头过身|沂水春风|瑰意琦行|小打小闹', '红杏枝头春意闹');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (611, 4, '似醉如痴|似曾相识|楚囚相对|高明远识|莺猜燕妒|众毁所归|往蹇来连', '似曾相识燕归来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (612, 4, '水米无交|莫可究诘|无如之奈|何所不至|簇锦团花|飘茵落溷|挂冠而去', '无可奈何花落去');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (613, 4, '大吹法螺|尽态极妍|风狂雨横|披沙简金|终始若一|糊涂到顶|金科玉条', '吹尽狂沙始到金');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (614, 4, '千里莼羹|沙里淘金|万象森罗|成语谜语|虽有搞暴|停辛伫苦|咽苦吐甘', '千淘万漉虽辛苦');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (615, 4, '不追既往|观过知仁|霜气横秋|思过半矣|落花无言|莫之谁何|家至户察', '不知秋思落谁家');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (616, 4, '按辔徐行|盐梅相成|食方于前|中馈之思|面誉背毁|玉汝于成|随高逐低', '行成于思毁于随');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (617, 4, '业绍箕裘|厉精更始|伊于胡底|勤劳勇敢|龙荒朔漠|燕燕于归|文恬武嬉', '业精于勤荒于嬉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (618, 4, '雨过天晴|匏瓜空悬|百不一存|鹤背扬州|排沙见金|高步云衢|从壁上观', '晴空一鹤排云上');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (619, 4, '因势乘便|繁称博引|七步成诗|缘情体物|面面皆到|一碧万顷|耸壑昂霄', '便引诗情到碧霄');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (620, 4, '浮笔浪墨|金瓶落井|惊师动众|风樯阵马|称雨道晴', '笔落惊风雨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (621, 4, '满腹诗书|地平天成|牛衣对泣|神出鬼入|神出鬼入', '诗成泣鬼神');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (622, 4, '朱甍碧瓦|白屋寒门|斗酒只鸡|肉袒负荆|入鲍忘臭', '朱门酒肉臭');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (623, 4, '路断人稀|腹有鳞甲|冰消冻释|破死忘生|刻骨崩心', '路有冻死骨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (624, 4, '滴露研珠|从井救人|尊古蔑今|衣绣夜行|白鱼入舟', '露从今夜白');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (625, 4, '停云落月|论列是非|诛故贳误|四乡八镇|孚尹明达', '月是故乡明');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (626, 4, '闭户读书|黄耳传书|破衲疏羹|万象森罗|胸中万卷', '读书破万卷');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (627, 4, '买上嘱下|心慕笔追|安堵如故|腹有鳞甲|神圣工巧', '下笔如有神');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (628, 4, '不立文字|句绘章|千磨百折|遗风古道|询事考言', '文章千古事');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (629, 4, '自业自得|悼心失图|尺短寸长|暗室亏心|叨在知己', '得失寸心知');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (630, 4, '尔汝之交|闲曹冷局|了身达命|习与体成|按名责实|泥沙俱下|灭景追风', '尔曹身与名俱灭');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (631, 4, '不可胜计|前功尽废|海沸江翻|表里山河|经纬万端|不今不古|枕石漱流', '不废江河万古流');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (632, 4, '昂首天外|超然迈伦|除残去秽|龙雕凤咀|饰词矫情', '天然去雕饰');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (633, 4, '拥彗清道|乐山乐水|在家出家|芙蓉并蒂|初发芙蓉', '清水出芙蓉');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (634, 4, '春风风人|分风劈流|疾足先得|出自意外|风樯阵马|寸蹄尺缣|疲癃残疾', '春风得意马蹄疾');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (635, 4, '青山一发|日月交食|佛眼相看|智尽力穷|五言长城|安常履顺|拈花一笑', '一日看尽长安花');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (636, 4, '荒烟野蔓|波光鳞鳞|陆海潘江|一箭上垛|论德使能|达人知命|遣愁索笑', '烟波江上使人愁');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (637, 4, '食日万钱|暮四朝三|靡然乡风|关情脉脉|不奈之何|安室利处|公是公非', '日暮乡关何处是');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (638, 4, '恫心疾首|风不鸣条|知我罪我|殊功劲节|铜驼草莽', '疾风知劲草');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (639, 4, '板上钉钉|荡检逾闲|多见广识|诚至金开|不臣之心', '板荡识诚臣');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (640, 4, '欲说还休|登坛拜将|须眉交白|毛发丝粟|引吭高唱|绿衣黄里|鸡尸牛从', '休将白发唱黄鸡');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (641, 4, '荆人涉|生聚教训|穷通得失|言简意该|男大须婚|灯尽油干|欢忻踊跃', '人生得意须尽欢');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (642, 4, '但求无过|甘心情愿|喙长三尺|如醉方醒|不世之功|e冈复岭|槁苏⑿', '但愿长醉不复醒');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (643, 4, '事不师古|往蹇来连|转凡为圣|博弈犹贤|触目皆是|寂天寞地|不甘寂寞', '古来圣贤皆寂寞');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (644, 4, '进退惟谷|亢龙有悔|餐风饮露|会者不忙|青史留名|其应若响|一文莫名', '惟有饮者留其名');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (645, 4, '百怪千奇|金钗换酒|云屯飙散|充类至尽|锦囊还矢|聊复尔尔|攘来熙往', '千金散尽还复来');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (646, 4, '噬脐莫及|轩之使|钿合金钗|俎樽折冲|马空冀北|临军对垒|日月入怀', '莫使金樽空对月');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (647, 4, '钧天广乐|抵死谩生|日不我与|绵里薄材|信赏必罚|亢龙有悔|调停两用', '天生我材必有用');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (648, 4, '振缨公朝|不绝如发|白齿青眉|丝竹八音|朝成暮|接袂成帷|冰魂雪魄', '朝如青丝暮成雪');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (649, 4, '扶危定倾|风樯阵马|独异于人|连明达夜|顺水行舟', '危樯独夜舟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (650, 4, '众星拱极|垂名青史|抱打不平|刮野扫地|海阔天高', '星垂平野阔');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (651, 4, '覆海移山|上A下报|白蜡明经|花朝月夜|通共有无|心潮澎湃|蓬生麻中', '海上明月共潮生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (652, 4, '长春不老|江南海北|风起潮涌|风行水上|浮湛连蹇|山珍海错|依流平进', '春江潮水连海平');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (653, 4, '江海之士|停云落月|无可如何|急景雕年|复旧如初|返视内照|金人三缄', '江月何年初照人');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (654, 4, '斗南一人|偕生之疾|代越庖俎|缓步代车|古井无波|穷大失居|自今已往', '人生代代无穷已');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (655, 4, '桂宫柏寝|骄儿I女|冠盖如云|月夕花朝|满谷满坑|春风沂水|殿堂楼阁', '宫女如花满春殿');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (656, 4, '贱敛贵出|行师动众|九鼎一丝|表里如一|真金烈火|控名责实|避世绝俗', '出师一表真名世');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (657, 4, '千载一弹|载酒问字|欲谁归罪|疲惫不堪|伯歌季舞|管仲随马|相得无间', '千载谁堪伯仲间');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (658, 4, '铁网珊瑚|束马悬车|春秋笔法|风前月下|高牙大纛|鸟惊鱼散|一夫当关', '铁马秋风大散关');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (659, 4, '横翔捷出|惊师动众|未形之患|避烦斗捷|了身脱命|广文先生|死且不朽', '出师未捷身先死');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (660, 4, '差三错四|顾盼生辉|捷报频传|烦心倦目|草头天子|郐下无讥|远虑深计', '三顾频烦天下计');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (661, 4, '日引月长|轩之使|英姿勃勃|雄辩强据|泪流满面|满腹文章|披襟解带', '长使英雄泪满襟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (662, 4, '腥闻在上|老天拔地|一厢情愿|交能易作|比肩接踵|攀鳞附翼|兽聚鸟散', '在天愿作比翼鸟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (663, 4, '大天白日|黄钟长弃|参天贰地|旷日引久|称家有无|风雨时若|竭智尽忠', '天长地久有时尽');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (664, 4, '岁在龙蛇|四战之地|心服情愿|詈夷为跖|浮湛连蹇|怡情理性|加枝添叶', '在地愿为连理枝');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (665, 4, '诸如此例|雨恨云愁|缠绵床第|绵延不绝|算无遗策|继绝存亡|为期不远', '此恨绵绵无绝期');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (666, 4, '食玉炊桂|雍容闲雅|寂然无声|不甘寂寞|怆然泪下|更阑人静|干霄蔽日', '玉容寂寞泪阑干');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (667, 4, '付之梨枣|月貌花容|日长一线|骈拇枝指|春深似海|抓纲带目|袂云汗雨', '梨花一枝春带雨');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (668, 4, '五角六张|故宫离黍|朱唇粉面|绮罗粉黛|盘游无度|先意承颜|怛然失色', '六宫粉黛无颜色');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (669, 4, '鹊返鸾回|星眸皓齿|太仓一粟|喜笑盈腮|本支百世|良辰媚景|蓬生麻中', '回眸一笑百媚生');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (670, 4, '向壁虚构|掘室求鼠|鼎铛有耳|遗风余俗|闲神野鬼', '虚室有余闲');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (671, 4, '恶贯久盈|叨在知己|鸟入樊笼|笼鸟槛猿|千里一曲', '久在樊笼里');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (672, 4, '故我依然|依翠偎红|华屋丘墟|惮赫千里|火灭烟消', '依依墟里烟');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (673, 4, '杀鸡为黍|鹤鸣之士|桑土之谋|江云渭树|没颠没倒', '鸡鸣桑树颠');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (674, 4, '放纵不羁|鸟惊鼠窜|三角恋爱|铸新淘旧|声振林木', '羁鸟恋旧林');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (675, 4, '持盈守虚|口巧手拙|菟裘归计|梨园子弟|殷殷田田', '守拙归园田');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (676, 4, '反老为少|覆亡无日|削足适履|一民同俗|幺弦孤韵', '少无适俗韵');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (677, 4, '临期失误|四纷五落|超轶绝尘|蛛网尘埃|当轴处中', '误落尘网中');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (678, 4, '此伏彼起|顾曲周郎|只轮莫返|声应气求|际地蟠天|上好下甚|有加无已', '此曲只应天上有');
INSERT INTO [sxs_poems_chengyu] ([id], [level], [question], [answer]) VALUES (679, 4, '酒阑人散|桑间濮上|登高能赋|腹有鳞甲|相差无几|参回斗转|多闻阙疑', '人间能有几回闻');
